import React, { useState } from 'react';
import './App.css';

function App() {
  const [todos, setTodos] = useState(['Take Dogs for a walk', 'Take the garbage out','mihir needs to play today']);
  const [input, setInput] = useState('');
  return (
    <div className="App">
      <h1> Hello world ❤️ </h1>
      <input value={input} onChange={event => setInput(event.target.value)}/>
      <button> Add Todo</button>

      <ul>
        {todos.map(todo => (
          <li>{todo}</li>
        ))}
      </ul>
    </div>
  );
}

export default App;
