import React, { useState } from 'react';
import { Button, FormControl, Input, InputLabel } from '@material-ui/core';
import './App.css';

function App() {
  const [todos, setTodos] = useState(['Take Dogs for a walk', 'Take the garbage out','mihir needs to play today']);
  const [input, setInput] = useState('');
  console.log('üëÄ', input);

  const addTodo = (event) => {
    //this will fire off when we click the button
    event.preventDefault(); //this will prevent from refreshing
    console.log('ü•∂', 'Im working!!!');
    setTodos([...todos, input]);
    setInput(''); //clear up the input after clicking add todo button
  }

  return (
    <div className="App">
      <h1> Hello world ‚ù§Ô∏è </h1>
      <form>
      {/* <input value={input} onChange={event => setInput(event.target.value)}/> */}

      <FormControl>
        <InputLabel> ‚úÖWrite a Todo</InputLabel>
        <Input value={input} onChange={event => setInput(event.target.value)}/>
      </FormControl>

      <Button disabled={!input} type="submit" onClick={addTodo} variant="contained" color="primary">
        Add ToDo
      </Button>
      {/* <button type="submit" onClick={addTodo}> Add Todo </button> */}
      </form>

      <ul>
        {todos.map(todo => (
          <li>{todo}</li>
        ))}
      </ul>
    </div>
  );
}

export default App;
